import { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  ul {
    list-style: none;
  }

  img {
    max-width: 100%;
  }

  .container {
    max-width: 1320px;
    margin: 0 auto;
    padding: 0 1.125rem;
  }

  @media (min-width: 1200px) {
    main {
      max-width: 1320px;
      margin: 0 auto;
    }

    .grid-container {
      display: grid;
      grid-template-columns: 850px auto;
      gap: 40px;
      max-width: 1320px;
      margin: 0 auto;
    }
  }
`;
