import React from 'react';
import { ListCart } from './style';
import { TiDelete } from 'react-icons/ti';

const CartProduct = ({ currentSale, setCurrentSale }) => {
  const removeItem = (e) => {
    // const itemId = e.target.closest('li').id;
    setCurrentSale((list) =>
      list.filter((elem) => elem.id !== e.target.closest('li').id),
    );

    // const removeItem = (item) => {
    //   setListTransactions((list) => list.filter((elem) => elem !== item));
    //   setListFilter((list) => list.filter((elem) => elem !== item));
    // };
  };

  return (
    <ListCart>
      {currentSale.map(({ id, name, category, img }) => (
        <li key={id} id={id}>
          <div className="productImg">
            <img src={img} alt={name} />
          </div>
          <div className="productInfo">
            <h3>{name}</h3>
            <span>{category}</span>
          </div>
          <button type="button" onClick={removeItem}>
            <TiDelete
              aria-label="Remover do carrinho"
              fontSize="32px"
              color="#f03e3e"
            />
          </button>
        </li>
      ))}
    </ListCart>
  );
};

export default CartProduct;
