import { createGlobalStyle } from 'styled-components';

export const GlobalStyle = createGlobalStyle`
  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  ul {
    list-style: none;
  }

  img {
    max-width: 100%;
  }

  .container {
    max-width: 1200px;
    margin: 0 auto;
    /* padding: 0 1.125rem; */
    box-sizing: border-box;
  }

  @media (min-width: 1200px) {
    .grid-container {
      display: grid;
      grid-template-columns: 800px 1fr;
    }
  }
`;
