import styled from 'styled-components';

export const ListCart = styled.ul`
  overflow-y: auto;
  width: 100%;

  &::-webkit-scrollbar {
    width: 12px; /* width of the entire scrollbar */
  }

  &::-webkit-scrollbar-track {
    background: orange; 
  }

  &::-webkit-scrollbar-thumb {
    background-color: blue; 
    border-radius: 20px; 
    border: 3px solid orange; 
  }
`;
