import React from 'react';
import Product from './Product';
import { List } from './style';

const ProductList = ({ products, handleClick, filteredProducts }) => {
  const renderList = () => {
    if (filteredProducts.length !== 0) {
      return (
        <>
          <h1>Teste</h1>
          {filteredProducts.map(({ id, name, category, img, price }) => (
            <Product
              key={id}
              id={id}
              name={name}
              category={category}
              img={img}
              price={price}
              handleClick={handleClick}
            />
          ))}
        </>
      );
    } else {
     return (
       products.map(({ id, name, category, img, price }) => (
        <Product
          key={id}
          id={id}
          name={name}
          category={category}
          img={img}
          price={price}
          handleClick={handleClick}
        />
      )))
    }
  };

  return (
    <main>
      <List>{renderList()}</List>
    </main>
  );
};

export default ProductList;
