import React, { useState } from 'react';
import { Button } from '../../../styles/button';
import { Form } from './style';
import { toast } from 'react-toastify';

export const InputSearch = ({ products, setFilteredProducts }) => {
  const [value, setValue] = useState('');
  const notify = () => toast.error('Produto não encontrado');

  const search = (value) => {
    const valueNormalize = value
      .normalize('NFD')
      .replace(/[\u0300-\u036f]/g, '');

    const filterProduct = products.filter((elem) => elem.name.includes(value));

    if (filterProduct.length) {
      setFilteredProducts(filterProduct);
      setValue('');
    } else {
      notify();
    }
  };

  return (
    <Form onSubmit={(e) => e.preventDefault(search(value))}>
      <input
        type="text"
        placeholder="Digite o produto"
        value={value}
        onChange={(e) => setValue(e.target.value)}
      />
      <Button type="submit">Pesquisar</Button>
    </Form>
  );
};
