import styled from 'styled-components';
import { Color } from '../../styles/color';
import { Font } from '../../styles/font';

export const CartStyled = styled.section`
  margin: 1rem 1.125rem 2rem 1.125rem;
  max-width: 100%;
  border-radius: 8px;
  background: ${Color.grey1};
  height: ${({ currentSale }) => (currentSale.length ? '200px' : '300px')};
  overflow-y: auto;
  position: relative;
  transition: 0.3s;

  h2 {
    background: ${Color.primary};
    color: ${Color.white};
    padding: 1rem;
    font: ${Font.title};
  }

  .cartInfo {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    gap: 1rem;
    height: 230px;
    padding: 1rem;

    p {
      font: ${Font.text1};
      color: ${Color.grey5};
    }

    span {
      font: ${Font.text4};
      color: ${Color.grey4};
    }
  }

  @media (min-width: 1200px) {
    margin: 2rem 0 2rem 0;
  }
`;
