import styled from 'styled-components';
import { Color } from '../../../styles/color';
import { Font } from '../../../styles/font';

export const Form = styled.form`
  border: 2px solid ${Color.grey2};
  border-radius: 8px;
  width: 100%;
  height: 50px;
  padding: 1rem;
  position: relative;
  outline-color: ${Color.primary};

  &::placeholder {
    color: ${Color.grey3};
    font: ${Font.text3};
  }
`;
