import React from 'react';
import { Button } from '../../styles/button';
import Product from './Product';
import { Title, List } from './style';

const ProductList = ({ products, handleClick, filteredProducts, value }) => {


  return (
    <main>
      {filteredProducts.length > 0 && (
        <Title>
          <h1>
            Resultados para:
            <span> {value}</span>
          </h1>
          <Button>Limpar busca</Button>
        </Title>
      )}
      <List>
        {filteredProducts.length ? (
          <>
            {filteredProducts.map(({ id, name, category, img, price }) => (
              <Product
                key={id}
                id={id}
                name={name}
                category={category}
                img={img}
                price={price}
                handleClick={handleClick}
              />
            ))}
          </>
        ) : (
          products.map(({ id, name, category, img, price }) => (
            <Product
              key={id}
              id={id}
              name={name}
              category={category}
              img={img}
              price={price}
              handleClick={handleClick}
            />
          ))
        )}
      </List>
    </main>
  );
};

export default ProductList;
