import styled from 'styled-components';
import { Color } from './color';
import { Font } from './font';

export const Button = styled.button`
  background: ${Color.primary};
  color: ${Color.white};
  border: none;
  border-radius: 8px;
  pad
  font: ${Font.text3};
`;
