import styled from 'styled-components';

export const ListCart = styled.ul`
  overflow-y: auto;
  width: 100%;

  &::-webkit-scrollbar {
    width: 12px;
  }

  &::-webkit-scrollbar-track {
    background: none;
  }

  &::-webkit-scrollbar-thumb {
    background-color: ${Color.primary};
    border-radius: 20px;
    border: 3px solid orange;
  }
`;
