import styled from 'styled-components';

export const CartStyled = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  border-top: 1px solid black;
  padding: 1rem;

  .totalInfo {
    display: flex;
    justify-content: space-between;
    margin-bottom: 1rem;
  }

  button {
    max-width: 100%;
  }
`;
