import styled from 'styled-components';
import { Font } from '../../../styles/font';

export const CartStyled = styled.div`
  position: absolute;
  bottom: 0;
  width: 100%;
  border-top: 1px solid black;
  padding: 1rem;

  .totalInfo {
    display: flex;
    justify-content: space-between;
    margin-bottom: 1rem;
  }

  button {
    width: 100%;
    height: 50px;
    font: ${Font.text1};
  }
`;
