import styled from 'styled-components';
import { Color } from '../../../styles/color';

export const ListCart = styled.ul`
  overflow-y: auto;
  width: 100%;

  &::-webkit-scrollbar {
    width: 10px;
  }

  &::-webkit-scrollbar-track {
    background: ${Color.grey2};
    border-radius: 8px;
  }

  &::-webkit-scrollbar-thumb {
    background-color: ${Color.primary};
    border-radius: 8px;
  }

  .productImg {

  }

`;
