import styled from 'styled-components';

export const List = styled.ul`
  display: flex;
  gap: 1.5rem;
  overflow-x: auto;
  padding: 2rem 1.125rem;
  width: 100%;

  @media (min-width: 1200px) {
    flex-wrap: wrap;
    padding: 2rem 0;
    overflow-x: auto;
  }
`;
