import React from 'react';
import { ListCart } from './style';
import { TiDelete } from 'react-icons/ti';
import 'animate.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const CartProduct = ({ currentSale, setCurrentSale }) => {
  const notify = () => toast.success('Produto adicionado ao carrinho');

  const removeItem = (item) => {
    setCurrentSale((list) => list.filter((elem) => elem !== item));
  };

  return (
    <>
      <ListCart>
        {currentSale.map((elem) => (
          <li
            key={elem.id}
            elem={elem.id}
            className="animate__animated animate__bounceInRight animate__fast"
          >
            <div className="productImg">
              <img src={elem.img} alt={elem.name} />
            </div>
            <div className="productInfo">
              <h3>{elem.name}</h3>
              <span>{elem.category}</span>
            </div>
            <button type="button" onClick={() => removeItem(elem)}>
              <TiDelete
                aria-label="Remover do carrinho"
                fontSize="32px"
                color="#f03e3e"
              />
            </button>
          </li>
        ))}
      </ListCart>
      <ToastContainer
        position="top-center"
        autoClose={1000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss={false}
        draggable
        pauseOnHover={false}
      />
    </>
  );
};

export default CartProduct;
